/* eslint-disable @typescript-eslint/no-explicit-any */
/* -------------------------------- Documents ------------------------------- */

declare module '*.html' {
  const document: any;
  export default document;
}
/* --------------------------------- Styles --------------------------------- */
declare module '*.scss' {
  const image: any;
  export default image;
}

/* --------------------------------- Images --------------------------------- */

declare module '*.gif' {
  const image: any;
  export default image;
}

declare module '*.jpg' {
  const image: any;
  export default image;
}

declare module '*.jpeg' {
  const image: any;
  export default image;
}

declare module '*.png' {
  const image: any;
  export default image;
}

declare module '*.svg' {
  const image: any;
  export default image;
}
